	SELECT o.*, r.refundNum AS refundNum

	FROM bo_order o
 	LEFT JOIN bo_refund r
 		ON o.idx = r.orderIdx

	WHERE o.memNickname = #{nickname} 


		AND o.orderStatus = #{sort}

	GROUP BY o.orderCode
	ORDER BY o.idx DESC LIMIT #{startIndexNo},#{pageSize};

