mapper.select:
SELECT r.*, b.title AS bookTitle, b.publisher AS publisher, b.authors AS authors, b.thumbnail AS thumbnail
FROM bo_reflection r LEFT JOIN bo_book b ON r.bookIdx = b.idx
WHERE r.idx = #{idx};

SELECT content, idx, edit FROM bo_reply WHERE idx IN

	 #{item.originIdx}

 AND replyDel != "삭제";

SELECT i.*, b.title AS bookTitle, b.authors AS authors, b.publisher AS publisher, (select idx from bo_insSave WHERE insIdx = i.idx AND memNickname = #{nickname}) AS insSaveIdx
FROM bo_inspired i LEFT JOIN bo_book b ON i.bookIdx = b.idx
WHERE i.memNickname = #{memNickname} AND i.insDel != "삭제" ORDER BY i.idx DESC LIMIT #{startIndexNo},#{pageSize};

SELECT *, b.thumbnail AS thumbnail, m.memPhoto AS memPhoto, (select count(*) from bo_reply p where r.idx = p.refIdx) AS replyNum
FROM bo_reflection r LEFT JOIN bo_book b ON r.bookIdx = b.idx LEFT JOIN bo_member m ON r.memNickname = m.nickname
WHERE r.refDel != "삭제" AND r.memNickname = #{memNickname}
ORDER BY r.idx DESC LIMIT #{startIndexNo},#{pageSize};


SELECT r.*, f.bookIdx AS bookIdx 

FROM bo_reply r
LEFT JOIN bo_reflection f
ON r.refIdx = f.idx 	

WHERE r.replyDel != "삭제" AND r.memNickname = #{memNickname} 
ORDER BY r.idx DESC LIMIT #{startIndexNo},#{pageSize};

SELECT * FROM bo_member WHERE memberDel != '탈퇴' 
AND nickname LIKE CONCAT('%',#{searchString},'%')
AND nickname NOT IN (SELECT blockedNickname FROM bo_block WHERE memNickname = #{memNickname});

